INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (132,'Compensacion','Cuando el Pokemon tiene su PS al maximo, esta habilidad reduce el dano recibido del ataque rival.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (133,'Competitivo','Los efectos de esta habilidad hacen que si al usuario le reducen una caracteristica, sube dos puntos de ataque.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (134,'Compiescolta','Esta habilidad le brinda un poco de proteccion ante ataques de aliados haciendo que el poseedor de esta habilidad reciba menos dano del normal.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (135,'Cosecha','El Pokemon puede utilizar la misma Baya de manera indefinida en batalla.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (136,'Cuerpo maldito','Todo aquel rival que golpee a un Pokemon con esta habilidad tiene un 30% de probabilidad de ver anulado temporalmente el uso del movimiento utilizado.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (137,'Espejo magico','Refleja el efecto de determinados movimientos modificadores.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (138,'Flaqueza','Si los PS restantes del Pokemon son inferiores al 50% de sus PS maximos, sus estadisticas se reducen.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (139,'Funda','Esta habilidad hace que el usuario sea invulnerable a los cambios del clima.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (140,'Herbivoro','Esta habilidad anula los posibles danos que el Pokemon que la tenga pueda sufrir al recibir ataques de tipo planta y aumenta su ataque cuando son golpeados por movimientos de dicho tipo.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (141,'Hurto','Hace que el usuario robe el objeto del rival al minimo contacto.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (142,'Ilusion','Permite transformarse en otros Pokemon.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (143,'impetu ardiente','Incrementa el poder de sus ataques especiales cuando el Pokemon esta quemado.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (144,'impetu arena','Esta habilidad aumenta la velocidad del Pokemon que la tiene cuando hay una tormenta de arena en plena batalla.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (145,'impetu toxico','Incrementa el poder los ataques fisicos cuando el usuario esta envenenado.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (146,'Impostor','El Pokemon se transforma automaticamente en el Pokemon rival al salir a la batalla. En batallas multiples se transforma en uno de sus rivales al azar.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (147,'Justiciero','Es una habilidad que da inmunidad ante los ataques de tipo siniestro y sube el ataque al recibir uno.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (148,'Metal liviano','Hace que el Pokemon pese la mitad');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (149,'Metal pesado','Hace que el Pokemon pese el doble.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (150,'Modo Daruma','Permite al pokemon usuario cambiar de forma cuando tenga menos del 50 % de PS, revirtiendo el Ataque por el Ataque especial.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (151,'Momia','Su efecto consiste en transformar la habilidad del oponente en Momia ');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (152,'Nerviosismo','Esta habilidad anula el uso de las bayas.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (153,'Piel milagro','Esta habilidad evita que el pokemon sea afectado por ataques que no hagan dano.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (154,'Poder arena','Esta habilidad aumenta el poder de los ataques de tipo tierra, roca y acero cuando hay una tormenta de arena en plena batalla.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (155,'Potencia bruta','Los efectos secundarios de los ataques no se cumplen, pero se hacen mas fuertes.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (156,'Punta acero','Provoca dano al rival que haga contacto con este Pokemon.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (157,'Regeneracion','Recupera un tercio de sus PS (HP) cuando lo regresas a su Pokeball en batalla.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (158,'Respondon','Los movimientos que suben estadisticas las bajan, y viceversa.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (159,'Sacapecho','Evita que le bajen la defensa al Pokemon usuario.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (160,'Telepatia','Con esta habilidad, ataques como terremoto o explosion no afectaran al Pokemon.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (161,'Terravoltaje','Hace que las otras habilidades no tengan efecto.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (162,'Tinovictoria','Aumenta la precision del Pokemon.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (163,'Toque toxico','Los movimientos de contacto del poseedor tienen una probabilidad del 30% de envenenar al oponente');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (164,'Turbollama','Hace que las otras habilidades no tengan efecto.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (165,'Veleta','Provoca que una de las estadisticas del Pokemon se incremente en dos niveles mientras otra se reduce en uno.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (166,'Alas vendaval','Otorga prioridad +1 a los movimientos de tipo volador.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (167,'Antibalas','Hace al usuario inmune a algunos movimientos especiales');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (168,'Amor filial','Permite atacar dos veces en un mismo turno, un ataque de su cria y otro del propio Pokemon.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (169,'Aura feerica','Potencia los movimientos de tipo Hada de todos los Pokemon en el combate.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (170,'Aura oscura','Potencia los movimientos de tipo Siniestro de todos los Pokemon en el combate.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (171,'Baba','Baja en un nivel la velocidad del Pokemon que use un movimiento de contacto contra el poseedor de la habilidad.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (172,'Cambio tactico','Cambia la forma y las estadisticas del Pokemon dependiendo de sus movimientos.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (173,'Carrillo','Cuando un Pokemon con esta habilidad se come una baya en combate, ademas de obtener el efecto habitual de esa baya, recuperara PS.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (174,'Garra dura','Aumenta el poder de los ataques fisicos de contacto.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (175,'Mar del albor','Cambia el clima a diluvio.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (176,'Megadisparador','Aumenta la potencia de movimientos de pulsos, como hidropulso y pulso umbrio, haciendolos aun mas devastadores.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (177,'Mandibula fuerte','Aumenta la potencia de movimientos en los que se requiere morder, como triturar o mordisco.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (178,'Manto frondoso','Aumenta la defensa especial del usuario si se usa el movimiento campo de hierba.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (179,'Mutatipo','Cambia el tipo del Pokemon al del movimiento que va a usar.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (180,'Pelaje recio','Reduce a la mitad el dano recibido por ataques fisicos.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (181,'Piel celeste','Cambia el tipo de los movimientos de tipo normal por tipo volador.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (182,'Piel feerica','Cambia el tipo de los movimientos de tipo normal por tipo hada.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (183,'Piel helada','Convierte los movimientos de tipo normal en tipo hielo.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (184,'Prestidigitador','Permite al usuario robar el objeto del oponente.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (185,'Rafaga delta','Cambia el clima aturbulencias en batalla');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (186,'Rompeaura','Invierte todas las habilidades de aura.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (187,'Simbiosis','Hace que el usuario sea capaz de pasar un objeto a un aliado.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (188,'Tenacidad','Si al usuario le reducen una caracteristica, sube dos puntos de ataque especial.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (189,'Tierra del ocaso','Cambia el clima a sol abrasador en batalla');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (190,'Velo aroma','Protege a los aliados de movimientos que afecten a su mente, como atraccion.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (191,'Velo dulce','Evita que los Pokemon aliados se duerman durante el combate.');
INSERT INTO  HabilidadPokemon (id_habilidadPokemon,nombre_habilidadPokemon,descripcion_habilidadPokemon) VALUES (192,'Velo flor','Evita que bajen las caracteristicas de los Pokemon de tipo planta aliados.');
